import { GET_PRODUCTS } from "../constants/productConstant";

const INITIAL_STATE = {
  products: [],
};

const productReducers = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case GET_PRODUCTS:
      return {
        ...state,
        products: action.payload,
      };

    default:
      return state;
  }
};

export default productReducers;
