import { GET_CATEGORIES } from "../constants/categoryConstants";

const INITIAL_STATE = {
  categories: [],
};
const categoryReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case GET_CATEGORIES:
      return {
        ...state,
        GET_CATEGORIES: action.payload,
      };

    default:
      return state;
  }
};

export default categoryReducer;
