import React, { Fragment, useEffect, useState } from "react";
import isEmpty from "validator/lib/isEmpty";
import { showErrorMsg, showSuccessMsg } from "./helpers/message";
import { showLoading } from "./helpers/loading";

//redux
import { useSelector, useDispatch } from "react-redux";
import { getProduct } from "../redux/actions/productActions";
import { getCategories } from "../redux/actions/categoryActions";

const AdminEditProduct = ({ match }) => {
  const dispatch = useDispatch();
  const productId = match.params.productId;
  const { product } = useSelector((state) => state.products);
  useEffect(() => {
    dispatch(getCategories());
  }, [dispatch]);

  useEffect(() => {
    if (!product) {
      dispatch(getProduct(productId));
    } else {
      setProductImage(product.fileName);
      setProductName(product.productName);
      setProductDesc(product.productDesc);
      setProductPrice(product.productPrice);
      setProductCategory(product.productCategory);
      setProductQty(product.productQty);
    }
    dispatch(getProduct(productId));
  }, [dispatch, productId]);

  //redux global state
  //from store
  const { successMsg, errorMsg } = useSelector((state) => state.messages);
  const { loading } = useSelector((state) => state.loading);
  const { categories } = useSelector((state) => state.categories);

  //
  const [clientSideError, setClientSideError] = useState("");
  const [productImage, setProductImage] = useState(null);
  const [productName, setProductName] = useState();
  const [productDesc, setProductDesc] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productCategory, setProductCategory] = useState("");
  const [productQty, setProductQty] = useState(null);

  /****************************
   * EVENT HANDLERS
   ***************************/

  /****** on typing food item *****/
  const handleProductImage = (e) => {
    //
  };
  const handleProductChange = (e) => {
    //
  };
  const handleProductSubmit = (evt) => {
    evt.preventDefault();
    //
  };
  return (
    <div className="container">
      <div className="modal-content">
        <form onSubmit={handleProductSubmit}>
          <div className="modal-header bg-warning text-dark">
            {clientSideError && showErrorMsg(clientSideError)}
            {errorMsg && showErrorMsg(errorMsg)}
            {successMsg && showSuccessMsg(successMsg)}

            <h5 className="modal-text">Add Food</h5>
          </div>
          <div className="modal-body my-3">
            {loading ? (
              <div className="text-center"> {showLoading()}</div>
            ) : (
              <Fragment>
                <div className="form-group mb-3">
                  <input
                    type="file"
                    className="form-control "
                    name="productImage"
                    onChange={handleProductImage}
                  />
                </div>
                <div className="form-group mt-4">
                  <label className="text-secondary"> Food Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="productName"
                    value={productName}
                    onChange={handleProductChange}
                  />
                </div>
                <div className="form-group">
                  <label className="text-secondary"> Description</label>
                  <textarea
                    type="text"
                    rows="4"
                    className="form-control"
                    name="productDesc"
                    value={productDesc}
                    onChange={handleProductChange}
                  ></textarea>
                </div>
                <div className="form-group">
                  <label className="text-secondary"> Price</label>
                  <input
                    type="text"
                    name="productPrice"
                    value={productPrice}
                    onChange={handleProductChange}
                    className="form-control"
                  />
                </div>
                <div className="form-row">
                  <div className="form-group col-md-4">
                    <label className="text-secondary"> Category</label>

                    <select
                      className="form-control custom-select mr-sm-2"
                      name="productCategory"
                      onChange={handleProductChange}
                    >
                      <option value="">Choose one..</option>
                      {categories &&
                        categories.map((c) => (
                          <option key={c._id} value={c._id}>
                            {c.category}
                          </option>
                        ))}
                    </select>
                  </div>
                  <div className="form-group col-md-4">
                    <label className="text-secondary"> Quantity</label>
                    <input
                      type="number"
                      min="1"
                      max="100"
                      className="form-control"
                      name="productQty"
                      value={productQty}
                      onChange={handleProductChange}
                    />
                  </div>
                </div>
              </Fragment>
            )}
          </div>
          <div className="modal-footer">
            <button type="submit" className="btn btn-warning">
              Save
            </button>
            <button className="btn btn-danger" data-dismiss="modal">
              Close
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminEditProduct;
