import {
  GET_PRODUCTS,
  CREATE_PRODUCT,
  DELETE_PRODUCT,
} from "../constants/productConstant";

const INITIAL_STATE = {
  products: [],
};

const productReducers = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CREATE_PRODUCT:
      return {
        products: [...state.products, action.payload],
      };
    case GET_PRODUCTS:
      return {
        ...state,
        products: action.payload,
      };
    case DELETE_PRODUCT:
      return {
        products: state.products.filter,
      };

    default:
      return state;
  }
};

export default productReducers;
