import React, { Fragment, useState, useEffect } from "react";
import { createCategory, getCategories } from "../api/category";
import isEmpty from "validator/lib/isEmpty";
import { showErrorMsg, showSuccessMsg } from "./helpers/message";
import { showLoading } from "./helpers/loading";
import { createProduct } from "../api/product";

const AdminDashboard = () => {
  const [categories, setCategories] = useState(null);
  const [category, setCategory] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");
  const [loading, setLoading] = useState(false);
  const [productData, setProductData] = useState({
    productImage: null,
    productName: "",
    productDesc: "",
    productPrice: "",
    productCategory: "",
    productQty: "",
  });
  const {
    productImage,
    productName,
    productDesc,
    productPrice,
    productCategory,
    productQty,
  } = productData;

  /******* LifeCycle Methods********/
  useEffect(() => {
    loadCategories();
  }, [loading]);

  //fetch category from api
  const loadCategories = async () => {
    await getCategories()
      .then((response) => {
        setCategories(response.data.categories);
        // console.log(categories);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
    setErrorMsg("");
    setSuccessMsg("");
  };

  /****** on typing food item *****/
  const handleProductImage = (e) => {
    console.log(e.target.files[0]);
    setProductData({
      ...productData,
      [e.target.name]: e.target.files[0],
    });
  };
  const handleProductChange = (e) => {
    setProductData({
      ...productData,
      [e.target.name]: e.target.value,
    });
  };

  const handleProductSubmit = (e) => {
    e.preventDefault();
    //client side validation
    if (productImage === null) {
      setErrorMsg("Please select an image");
    } else if (
      isEmpty(productName) ||
      isEmpty(productDesc) ||
      isEmpty(productPrice)
    ) {
      setErrorMsg("All fields are required");
    } else if (isEmpty(productCategory)) {
      setErrorMsg("Please select product category");
    } else if (isEmpty(productQty)) {
      setErrorMsg("Please select product quantity");
    } else {
      let formData = new FormData();
      formData.append("productImage", productImage);
      formData.append("productName", productName);
      formData.append("productDesc", productDesc);
      formData.append("productPrice", productPrice);
      formData.append("productCategory", productCategory);
      formData.append("productQty", productQty);

      createProduct(formData)
        .then((response) => {
          setProductData({
            productImage: null,
            productName: "",
            productDesc: "",
            productPrice: "",
            productCategory: "",
            productQty: "",
          });
          setSuccessMsg(response.data.successMessage);
          console.log("Server response:", response);
        })
        .catch((err) => {
          console.log(err);
          setErrorMsg(err.response.data.errorMessage);
        });
    }
  };

  //clear success and error message when modal close
  const handleMessages = (e) => {
    setErrorMsg("");
    setSuccessMsg("");
  };
  const handleCategorySubmit = (e) => {
    e.preventDefault();

    //client side validation(validator)
    if (isEmpty(category)) {
      setErrorMsg("Please enter a category");
    } else {
      //success
      const data = { category };
      //request to api
      setLoading(true);
      createCategory(data)
        .then((response) => {
          setLoading(false);
          setSuccessMsg(response.data.successMessage);
          setCategory("");
        })
        .catch((err) => {
          setLoading(false);
          setErrorMsg(err.response.data.errorMessage);
        });
    }
  };
  /***** Views ****** */
  const showHeader = () => (
    <div className="bg-secondary text-white py-4">
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <h1>
              <i className="fas fa-home"> Dashboard</i>
            </h1>
          </div>
        </div>
      </div>
    </div>
  );
  /******* Modal Button ********/
  const showActionsBtns = () => (
    <div className="bg-light my-2">
      <div className="container">
        <div className="row pb-4">
          <div className="col-md-4 my-1">
            <button
              className=" btn btn-info btn-block"
              data-toggle="modal"
              data-target="#addCategoryModal"
            >
              <i className="fas fa-plus">Add Category</i>
            </button>
          </div>
          <div className="col-md-4 my-1">
            <button
              className=" btn btn-warning btn-block"
              data-toggle="modal"
              data-target="#addFoodModal"
            >
              <i className="fas fa-plus"> Add Food</i>
            </button>
          </div>
          <div className="col-md-4 my-1">
            <button className=" btn btn-success btn-block">
              <i className="fas fa-money-check"> View Orders</i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
  /******* Category Modal window ********/
  const showCategoryModal = () => (
    <div id="addCategoryModal" className="modal fade" onClick={handleMessages}>
      <div className="modal-dialog modal-dialog-centered modal-lg">
        <div className="modal-content">
          <form onSubmit={handleCategorySubmit}>
            <div className="modal-header bg-info text-white">
              {errorMsg && showErrorMsg(errorMsg)}
              {successMsg && showSuccessMsg(successMsg)}

              <h5 className="modal-text">Add Category</h5>
              <button className="close">
                <span>
                  <i
                    className="fas fa-times text-white"
                    data-dismiss="modal"
                  ></i>
                </span>
              </button>
            </div>
            <div className="modal-body my-3">
              {loading ? (
                <div className="text-center"> {showLoading()}</div>
              ) : (
                <>
                  <label className="text-dark">Category Name</label>
                  <input
                    type="text"
                    name="category"
                    value={category}
                    className="form-control"
                    onChange={handleCategoryChange}
                  />
                </>
              )}
            </div>
            <div className="modal-footer">
              <button type="submit" className="btn btn-success">
                Save
              </button>
              <button className="btn btn-danger" data-dismiss="modal">
                Close
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );

  /******* Food Modal(product) window ********/
  const showFoodModal = () => (
    <div id="addFoodModal" className="modal fade" onClick={handleMessages}>
      <div className="modal-dialog modal-dialog-centered modal-lg">
        <div className="modal-content">
          <form onSubmit={handleProductSubmit}>
            <div className="modal-header bg-warning text-dark">
              {errorMsg && showErrorMsg(errorMsg)}
              {successMsg && showSuccessMsg(successMsg)}

              <h5 className="modal-text">Add Food</h5>
              <button className="close">
                <span>
                  <i
                    className="fas fa-times text-dark"
                    data-dismiss="modal"
                  ></i>
                </span>
              </button>
            </div>
            <div className="modal-body my-3">
              {loading ? (
                <div className="text-center"> {showLoading()}</div>
              ) : (
                <Fragment>
                  <div className="form-group mb-3">
                    <input
                      type="file"
                      className="form-control "
                      name="productImage"
                      onChange={handleProductImage}
                    />
                  </div>
                  <div className="form-group mt-4">
                    <label className="text-secondary"> Food Name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="productName"
                      value={productName}
                      onChange={handleProductChange}
                    />
                  </div>
                  <div className="form-group">
                    <label className="text-secondary"> Description</label>
                    <textarea
                      type="text"
                      rows="4"
                      className="form-control"
                      name="productDesc"
                      value={productDesc}
                      onChange={handleProductChange}
                    ></textarea>
                  </div>
                  <div className="form-group">
                    <label className="text-secondary"> Price</label>
                    <input
                      type="text"
                      name="productPrice"
                      value={productPrice}
                      onChange={handleProductChange}
                      className="form-control"
                    />
                  </div>
                  <div className="form-row">
                    <div className="form-group col-md-4">
                      <label className="text-secondary"> Category</label>

                      <select
                        className="form-control custom-select mr-sm-2"
                        name="productCategory"
                        onChange={handleProductChange}
                      >
                        <option value="">Choose one..</option>
                        {categories &&
                          categories.map((c) => (
                            <option key={c._id} value={c._id}>
                              {c.category}
                            </option>
                          ))}
                      </select>
                    </div>
                    <div className="form-group col-md-4">
                      <label className="text-secondary"> Quantity</label>
                      <input
                        type="number"
                        min="1"
                        max="100"
                        className="form-control"
                        name="productQty"
                        value={productQty}
                        onChange={handleProductChange}
                      />
                    </div>
                  </div>
                </Fragment>
              )}
            </div>
            <div className="modal-footer">
              <button type="submit" className="btn btn-warning">
                Save
              </button>
              <button className="btn btn-danger" data-dismiss="modal">
                Close
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
  /***** Render ****/
  return (
    <section>
      {JSON.stringify(productData)}
      {showHeader()}
      {showActionsBtns()}
      {showCategoryModal()}
      {showFoodModal()}
    </section>
  );
};

export default AdminDashboard;
